pragma solidity ^0.6.2;

/**
 * @title Carbon's Prime Contract
 * @notice The core ERC721 contract that holds all Slates.
 * @author Carbon
 */

import "./tokens/SafeMath.sol";
import "./Slate.sol";


abstract contract ERC20 {
    function balanceOf(address _owner) virtual external returns(uint256);
    function transferFrom(address sender, address recipient, uint256 amount) public virtual returns (bool);
    function transfer(address recipient, uint256 amount) public virtual returns (bool);
}

// abstract contract Slate {
//     function mint(address to, uint256 tokenId, string memory tokenURI) public virtual returns (bool);
//     function burn(address owner, uint256 tokenId) public virtual returns (bool);
//     function ownerOf(uint256 tokenId) public view virtual returns (address);
//     function balanceOf(address owner) public view virtual returns (uint256);
// }

abstract contract IPrime {
    /**
    * @dev Emitted on new slate.
    * @param _user Address of user.
    * @param _asset Address if deposited asset.
    * @param _amount Quantity of deposited asset.
    * @param _tokenId Nonce of minted Slate -> ID.
    * @param _timestamp Block.timestamp of deposit.
    **/
    event CreateSlate(
        address indexed _user,
        address indexed _asset, 
        uint256 _amount, 
        uint256 indexed _tokenId, 
        uint256 _timestamp
    );

    event Exercise(
        address indexed _user, 
        address indexed _asset, 
        uint256 _amount, 
        uint256 indexed _tokenId, 
        uint256 _timestamp
    );

    event Close(
        address indexed _user, 
        address indexed _asset, 
        uint256 _amount, 
        uint256 indexed _tokenId, 
        uint256 _timestamp
    );

    event CollateralUpdate(
        uint256 _amount, 
        uint256 indexed _tokenId, 
        uint256 _timestamp
    );

    event SlateMinted(
        uint256 indexed _tokenId, 
        uint256 _timestamp
    );

    //function initialize(address _ocall) public virtual returns (bool);
    function createSlate(uint256 _xis, address _yak, uint256 _zed, address _wax, uint256 _pow, address _gem) public virtual returns (bool);
    function exercise(uint256 _tokenId) public virtual returns (bool);
    function withdraw(uint256 _amount, address _addr) public virtual returns (bool);
    function close(uint256 _tokenId) public virtual returns (bool);
    function getCollateralAmount(uint256 _tokenId) public view virtual returns (uint256);
}


contract Prime is IPrime, Slate("Prime Contract", "SP") {
    using SafeMath for uint256;

    /** 
     * @dev A slate is the defining traits of the NFT prime.
     * @param ace `msg.sender` of the createSlate function.
     * @param xis Quantity of collateral asset token.
     * @param yak Address of collateral asset token.
     * @param zed Purchase price of collateral, denominated in quantity of token z.
     * @param wax Address of purchase price asset token.
     * @param pow UNIX timestamp of valid time period.
     * @param gem Address of payment receiver of token z.
     */
    struct Slates {
        address ace;
        uint256 xis;
        address yak;
        uint256 zed;
        address wax;
        uint256 pow;
        address gem;
    }

    string constant URI = '';
    uint256 constant INCREMENT = 1;
    Slates public _slate;
    uint256 public oNonce;
    
    mapping(uint256 => Slates) public _slates; // NFT ID -> Slate.
    mapping(uint256 => uint256) public _collateral; // NFT ID -> collateral amount.
    mapping(address => mapping(address => uint256)) private _bank; // Users -> asset -> withdrawable balance.

    ///** 
    // * @dev `msg.sender` Sets the NFT and controller address.
    // * @param slate Address of NFT contract.
    // * @return success
    // */
    //function initialize(address slate) public override returns (bool) {
    //    _slate = Slate(slate);
    //    return true;
    //}

    /** 
     * @dev `msg.sender` Deposits asset x, mints new Slate.
     * @param _xis Amount of collateral to deposit.
     * @param _yak Address of collateral asset.
     * @param _zed Amount of payment asset.
     * @param _wax Payment asset address.
     * @param _pow Expiry timestamp.
     * @param _gem Receiver address.
     * @return success
     */
    function createSlate(
            uint256 _xis,
            address _yak,
            uint256 _zed,
            address _wax,
            uint256 _pow,
            address _gem
        ) public override returns (bool success) {
        // CHECKS
        ERC20 yak = ERC20(_yak);
        uint256 bal = yak.balanceOf(msg.sender);
        require(bal >= _xis, 'Cannot send amount > bal');

        // EFFECTS
        _incrementNonce();
        _slates[oNonce] = Slates(msg.sender, _xis, _yak, _zed, _wax, _pow, _gem);
        _updateCollateral(_xis, oNonce);
        _bank[msg.sender][_yak] = _xis; // Depositor can withdraw collateral

        // INTERACTIONS
        emit CreateSlate(msg.sender, _yak, _xis, oNonce, block.timestamp);
        mint(msg.sender, oNonce, URI);
        return yak.transferFrom(msg.sender, address(this), _xis);
    }

    /** 
     * @dev `msg.sender` Exercises right to purchase collateral.
     * @param _tokenId ID of Slate.
     * @return success
     */
    function exercise(uint256 _tokenId) public override returns (bool) {
        // CHECKS
        uint256 bal = balanceOf(msg.sender);
        require(bal >= 1, 'Cannot send amount > bal');

        address own = ownerOf(_tokenId);
        require(own == msg.sender, 'Owner is not sender');

        address ace = _slates[_tokenId].ace;
        uint256 xis = _slates[_tokenId].xis;
        address yak = _slates[_tokenId].yak;
        uint256 zed = _slates[_tokenId].zed;
        address wax = _slates[_tokenId].wax;
        uint256 pow = _slates[_tokenId].pow;
        address gem = _slates[_tokenId].gem;
        ERC20 _yak = ERC20(_slates[_tokenId].yak);
        ERC20 _wax = ERC20(_slates[_tokenId].wax);

        require(_wax.balanceOf(msg.sender) >= zed, 'Bal < payment amount');
        require(pow >= block.timestamp, 'Expired Slate');

        // EFFECTS
        require(_wax.transferFrom(msg.sender, address(this), zed), 'Payment not transferred');
        _bank[ace][yak] = 0; // Depositor cannot withdraw collateral x.
        _bank[gem][wax] = zed; // Payment receiver can withdraw payment z.
        _bank[msg.sender][yak] = xis; // Payer can withdraw collateral x.

        // INTERACTIONS
        emit Exercise(msg.sender, wax, zed, _tokenId, block.timestamp);
        return burn(msg.sender, _tokenId);
    }

    /** 
     * @dev `msg.sender` Close Slate and withdraw collateral as Slate minter.
     * @param _tokenId Slate NFT ID to close.
     * @return success
     */
    function close(uint256 _tokenId) public override returns (bool) {
        // CHECKS
        uint256 bal = balanceOf(msg.sender);
        require(bal >= 1, 'Cannot send amount > bal');

        address own = ownerOf(_tokenId);
        require(own == msg.sender, 'Owner is not sender');

        address ace = _slates[_tokenId].ace;
        uint256 xis = _slates[_tokenId].xis;
        address yak = _slates[_tokenId].yak;
        uint256 pow = _slates[_tokenId].pow;
        ERC20 _yak = ERC20(_slates[_tokenId].yak);

        require(msg.sender == ace, 'User is not Slate creator');
        require(_bank[msg.sender][yak] > 0, 'User has no collateral to claim');
        require(pow >= block.timestamp, 'Expired Slate');

        // EFFECTS
        _bank[msg.sender][yak] = 0;

        // INTERACTIONS
        emit Close(msg.sender, yak, xis, _tokenId, block.timestamp);
        burn(msg.sender, _tokenId);
        return _yak.transfer(msg.sender, xis);
    }

    /** 
     * @dev `msg.sender` Withdraw assets from contract.
     * @param _amount Quantity to withdraw.
     * @param _addr Address of asset.
     * @return success
     */
    function withdraw(uint256 _amount, address _addr) public override returns (bool) {
        // CHECKS
        uint256 bank = _bank[msg.sender][_addr];
        require(bank >= _amount, 'Cannot withdraw amount > bal');

        // INTERACTIONS 
        ERC20 erc20 = ERC20(_addr);
        return erc20.transfer(msg.sender, _amount);
    }

    /** 
     * @dev Utility to update [tokenId] = collateral mapping.
     * @param _amount Quantity of Slate Collateral.
     * @param _tokenId Nonce of Slate.
     */
    function _updateCollateral(uint256 _amount, uint256 _tokenId) internal {
        _collateral[_tokenId] = _amount;
        emit CollateralUpdate(_amount, _tokenId, block.timestamp);
    }

    /** 
     * @dev Utility to update tokenId Nonce.
     * @return Nonce.
     */
    function _incrementNonce() internal returns (uint256) {
        oNonce = oNonce.add(INCREMENT);
        emit SlateMinted(oNonce, block.timestamp);
        return oNonce;
    }

    /** 
     * @dev `msg.sender` Get Collateral tied to token ID.
     * @param _tokenId Nonce of token.
     * @return uint256 Collateral quantitiy.
     */
    function getCollateralAmount(uint256 _tokenId) public view override returns (uint256) {
        return _collateral[_tokenId];
    }
}

